<style type="text/css">body{font-size:0.75em;}</style><script type="text/javascript">
var resizeFontSizes = ["0.421875em","0.5625em","0.75em","0.9375em","1.171875em","1.46484375em","1.8310546875em"];
var resizeCookieName = 'textSize';
var resizeCookieExpireDays = 15;
var resizeCurrentSizeIndex = 2;

function decreaseTextSize()
{
    if($ && $.cookie) {
	    if (resizeCurrentSizeIndex > 0)
	    {
		    var currentFontSize = resizeFontSizes[--resizeCurrentSizeIndex];
		    $.cookie(resizeCookieName, resizeCurrentSizeIndex, { expires: resizeCookieExpireDays});
		    $('body').css('font-size', currentFontSize);
            return false;
	    }
    }
    return true;
}

function increaseTextSize()
{
    if($ && $.cookie) {
	    if (resizeCurrentSizeIndex < resizeFontSizes.length - 1)
	    {
		    var currentFontSize = resizeFontSizes[++resizeCurrentSizeIndex];
            $.cookie(resizeCookieName, resizeCurrentSizeIndex, { expires: resizeCookieExpireDays});
		    $('body').css('font-size', currentFontSize);
            return false;
	    }
    }
    return true;
}
